CREATE TABLE "public"."social_following" ("id" uuid NOT NULL, "follower_id" uuid NOT NULL, "followee_id" uuid NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("follower_id") REFERENCES "public"."user"("user_id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("followee_id") REFERENCES "public"."user"("user_id") ON UPDATE restrict ON DELETE restrict, UNIQUE ("id"));COMMENT ON TABLE "public"."social_following" IS E'This junction table records social following relationships between users. Each record represents a follower-followee relationship.';
