CREATE TABLE "public"."message" ("message_id" serial NOT NULL, "content" text NOT NULL, "sender_id" integer NOT NULL, "receiver_id" integer NOT NULL, "type" text NOT NULL, PRIMARY KEY ("message_id") , FOREIGN KEY ("sender_id") REFERENCES "public"."user"("user_id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("sender_id") REFERENCES "public"."chatbot"("chatbot_id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("receiver_id") REFERENCES "public"."user"("user_id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("receiver_id") REFERENCES "public"."chatbot"("chatbot_id") ON UPDATE restrict ON DELETE restrict, UNIQUE ("message_id"));COMMENT ON TABLE "public"."message" IS E'This table stores the messages exchanged between users and chatbots.';
