CREATE TABLE "public"."user_chatbot_preference" ("preference_id" serial NOT NULL, "user_id" integer NOT NULL, "chatbot_id" integer NOT NULL, "preferred_tone" text NOT NULL, "preferred_length" text NOT NULL, "preferred_type" text NOT NULL, "preferred_complexity" text NOT NULL, PRIMARY KEY ("preference_id") , FOREIGN KEY ("preferred_tone") REFERENCES "public"."default_tone_enum"("value") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("preferred_length") REFERENCES "public"."default_length_enum"("value") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("preferred_type") REFERENCES "public"."default_type_enum"("value") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("preferred_complexity") REFERENCES "public"."default_complexity_enum"("value") ON UPDATE restrict ON DELETE restrict, UNIQUE ("preference_id"), UNIQUE ("user_id", "chatbot_id"));COMMENT ON TABLE "public"."user_chatbot_preference" IS E'This table stores user-specific preferences for quick access when they interact with a chatbot.';
